import React, { useState, useEffect, useRef } from 'react';
import { v4 as uuidv4 } from 'uuid';
import { Plus, MessageSquare, Paperclip, ArrowUp, Loader, User, Bot } from 'lucide-react';

// You can replace this with your actual logo import
const YourLogo = ({ className }) => (
  <svg className={className} viewBox="0 0 100 100" xmlns="http://www.w3.org/2000/svg">
    <defs>
      <linearGradient id="logoGradient" x1="0%" y1="0%" x2="100%" y2="100%">
        <stop offset="0%" style={{stopColor: '#a855f7', stopOpacity: 1}} />
        <stop offset="100%" style={{stopColor: '#3b82f6', stopOpacity: 1}} />
      </linearGradient>
    </defs>
    <circle cx="50" cy="50" r="45" fill="none" stroke="url(#logoGradient)" strokeWidth="8"/>
    <text x="50" y="62" fontFamily="Arial, sans-serif" fontSize="40" fill="url(#logoGradient)" textAnchor="middle" fontWeight="bold">C</text>
  </svg>
);


// --- Main App Component ---
function App() {
  const [conversations, setConversations] = useState({});
  const [activeConversationId, setActiveConversationId] = useState(null);
  const [prompt, setPrompt] = useState('');
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [userId] = useState(() => uuidv4()); // Ensure userId is stable
  const messagesEndRef = useRef(null);
  const fileInputRef = useRef(null);

  // Effect to scroll to the latest message
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [conversations, activeConversationId]);

  // Function to create a new chat session
  const createNewConversation = () => {
    const newId = uuidv4();
    setConversations(prev => ({
      ...prev,
      [newId]: { id: newId, name: `New Chat`, messages: [] }
    }));
    setActiveConversationId(newId);
  };

  // Start with a new conversation if none exist
  useEffect(() => {
    if (Object.keys(conversations).length === 0) {
      createNewConversation();
    }
  }, []);


  const handleSubmit = async (e) => {
    e.preventDefault();
    if ((!prompt.trim() && !file) || !activeConversationId) return;

    setError(null);
    setLoading(true);

    const userMessage = {
      id: uuidv4(),
      role: 'user',
      text: prompt,
      file: file ? { name: file.name, type: file.type } : null,
      timestamp: new Date().toISOString(),
    };

    // Update state immediately for a responsive feel
    setConversations(prev => ({
      ...prev,
      [activeConversationId]: {
        ...prev[activeConversationId],
        name: prev[activeConversationId].messages.length === 0 ? (prompt.substring(0, 25) + '...') : prev[activeConversationId].name,
        messages: [...prev[activeConversationId].messages, userMessage],
      }
    }));
    setPrompt('');
    setFile(null);

    const formData = new FormData();
    formData.append('prompt', prompt);
    formData.append('user_id', userId);
    if (file) {
      formData.append('file', file);
    }

    try {
      const response = await fetch('http://127.0.0.1:8000/chat/', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();

      // The backend returns the full history, we just need the last message from the bot
      const botMessageData = data[data.length - 1];

      const botMessage = {
        id: botMessageData.id,
        role: 'bot',
        text: botMessageData.text || 'Sorry, I received an empty response.',
        file_url: botMessageData.file_url,
        timestamp: botMessageData.timestamp,
      };

      setConversations(prev => ({
        ...prev,
        [activeConversationId]: {
          ...prev[activeConversationId],
          messages: [...prev[activeConversationId].messages, botMessage],
        }
      }));

    } catch (err) {
      console.error(err);
      const errorMessage = {
        id: uuidv4(),
        role: 'bot',
        text: 'âŒ Error: Failed to connect to the backend. Please ensure it is running and accessible.',
        timestamp: new Date().toISOString(),
      };
      setConversations(prev => ({
        ...prev,
        [activeConversationId]: {
          ...prev[activeConversationId],
          messages: [...prev[activeConversationId].messages, errorMessage],
        }
      }));
    } finally {
      setLoading(false);
    }
  };

  const activeMessages = conversations[activeConversationId]?.messages || [];

  return (
    <>
      {/* This style tag contains the animation for the logo */}
      <style>
        {`
          @keyframes pulse {
            0%, 100% {
              transform: scale(1);
              opacity: 1;
            }
            50% {
              transform: scale(1.05);
              opacity: 0.9;
            }
          }
          .logo-pulse {
            animation: pulse 4s ease-in-out infinite;
          }
        `}
      </style>
      <div className="flex h-screen w-screen bg-gray-800 text-white font-sans">
        {/* --- Sidebar --- */}
        <Sidebar 
          conversations={Object.values(conversations)}
          activeConversationId={activeConversationId}
          setActiveConversationId={setActiveConversationId}
          createNewConversation={createNewConversation}
        />

        {/* --- Main Chat View --- */}
        <ChatView
          messages={activeMessages}
          loading={loading}
          prompt={prompt}
          setPrompt={setPrompt}
          setFile={setFile}
          handleSubmit={handleSubmit}
          messagesEndRef={messagesEndRef}
          fileInputRef={fileInputRef}
          file={file}
        />
      </div>
    </>
  );
}

