import React, { useState, useRef, useEffect } from "react";

function App() {
  const [prompt, setPrompt] = useState("");
  const [file, setFile] = useState(null);
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    // Load history on mount
    fetch("http://127.0.0.1:8000/history/")
      .then(res => res.json())
      .then(setMessages);
  }, []);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!prompt.trim() && !file) return;
    setLoading(true);

    const formData = new FormData();
    formData.append("prompt", prompt);
    if (file) formData.append("file", file);

    const res = await fetch("http://127.0.0.1:8000/chat/", {
      method: "POST",
      body: formData,
    });
    const data = await res.json();
    setMessages(data);
    setPrompt("");
    setFile(null);
    setLoading(false);
  };

  const renderFile = (msg) => {
    if (!msg.file_url) return null;
    if (msg.file_type === "image") {
      return <img src={`http://127.0.0.1:8000${msg.file_url}`} alt="upload" className="max-w-xs my-2 rounded" />;
    }
    return (
      <a href={`http://127.0.0.1:8000${msg.file_url}`} target="_blank" rel="noopener noreferrer" className="text-blue-600 underline">
        {msg.file_url.split("/").pop()}
      </a>
    );
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col items-center py-8">
      <div className="w-full max-w-xl bg-white rounded-xl shadow-xl p-6 flex flex-col gap-4 border border-gray-200">
        <div className="h-96 overflow-y-auto px-2">
          {messages.map((msg) => (
            <div
              key={msg.id}
              className={`mb-4 p-3 max-w-[80%] whitespace-pre-line rounded-xl text-sm shadow ${
                msg.role === "user"
                  ? "ml-auto bg-purple-100 text-right text-purple-800"
                  : "mr-auto bg-gray-100 text-left text-gray-800"
              }`}
            >
              {msg.text}
              {renderFile(msg)}
              <div className="text-xs text-gray-400 mt-1">{new Date(msg.timestamp).toLocaleTimeString()}</div>
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>
        <form onSubmit={handleSubmit} className="flex gap-2 items-center w-full">
          <textarea
            className="flex-1 p-3 border border-gray-300 rounded-lg resize-none shadow-sm focus:outline-purple-500"
            placeholder="Type your message..."
            rows={2}
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
          />
          <input
            type="file"
            accept="image/*,.pdf,.txt,.doc,.docx"
            className="hidden"
            id="file-input"
            onChange={(e) => setFile(e.target.files[0])}
          />
          <label htmlFor="file-input" className="cursor-pointer bg-purple-100 hover:bg-purple-200 text-purple-700 font-semibold py-2 px-4 rounded-lg text-sm">
            ðŸ“Ž File
          </label>
          <button
            type="submit"
            className="bg-purple-600 text-white px-4 py-2 rounded-lg hover:bg-purple-700"
            disabled={loading}
          >
            {loading ? "Uploading..." : "Send"}
          </button>
        </form>
        {file && <p className="text-sm text-gray-500 ml-2">ðŸ“„ {file.name}</p>}
      </div>
      <footer className="mt-6 text-xs text-gray-400">Made with ðŸ’œ | LLM Recommender</footer>
    </div>
  );
}

export default App;
