import React, { useState } from 'react';
import logo from './logo.jpg'; // Use your uploaded logo image path

function App() {
  const [prompt, setPrompt] = useState('');
  const [file, setFile] = useState(null);
  const [models, setModels] = useState([]);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setModels([]);

    try {
      const response = await fetch('http://127.0.0.1:8000/recommend', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ prompt }),
      });

      const data = await response.json();

      if (data.models && data.models.length > 0) {
        setModels(data.models);
      } else {
        setError("No models found for your prompt.");
      }
    } catch (error) {
      console.error('Error:', error);
      setError('Failed to connect to backend');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-100 via-white to-blue-100 flex flex-col items-center px-4 py-10">
      <div className="max-w-3xl w-full bg-white shadow-2xl rounded-2xl p-8 border border-gray-200">
        <div className="flex items-center justify-center mb-6">
          <img src={logo} alt="Caze Labs Logo" className="w-12 h-12 mr-3 rounded-full" />
          <h1 className="text-4xl md:text-5xl font-extrabold text-purple-700 tracking-tight">
            LLM Recommender <span className="ml-2">🤖</span>
          </h1>
        </div>

        <p className="text-center text-gray-500 mb-8">
          Enter your use case or upload a file. We’ll recommend the best Large Language Models!
        </p>

        <form onSubmit={handleSubmit} className="space-y-6">
          <textarea
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            placeholder="e.g., Summarize a legal document"
            rows={5}
            className="w-full p-4 border border-gray-300 rounded-xl shadow-sm focus:outline-none focus:ring-2 focus:ring-purple-500"
          />

          <div className="flex flex-col sm:flex-row sm:items-center gap-4">
            <input
              type="file"
              onChange={(e) => setFile(e.target.files[0])}
              className="text-sm w-full sm:w-auto file:mr-4 file:py-2 file:px-4
              file:rounded-lg file:border-0
              file:text-sm file:font-semibold
              file:bg-purple-100 file:text-purple-700
              hover:file:bg-purple-200"
            />
            <button
              type="submit"
              className="w-full sm:w-auto px-6 py-2 bg-purple-600 hover:bg-purple-700 text-white rounded-lg font-medium transition duration-300"
            >
              Get LLMs 🚀
            </button>
          </div>
        </form>

        <div className="mt-8">
          {models.length > 0 && (
            <div className="bg-purple-50 p-4 rounded-lg shadow-inner border border-purple-200">
              <h2 className="text-xl font-semibold mb-2 text-purple-800">Recommended Models:</h2>
              <ul className="space-y-2">
                {models.map((model, index) => (
                  <li key={index} className="text-sm text-gray-800">
                    <strong>Name:</strong> {model.name} <br />
                    <strong>License:</strong> {model.license} <br />
                    <strong>Reason:</strong> {model.reason}
                  </li>
                ))}
              </ul>
            </div>
          )}

          {error && (
            <div className="mt-4 text-red-500 text-sm text-center">{error}</div>
          )}
        </div>
      </div>

      <footer className="mt-12 text-gray-400 text-xs text-center">
        Built with 💜 by Team Avial-AI-002 | Caze Labs Internship
      </footer>
    </div>
  );
}

export default App;
