import React, { useState, useEffect, useRef } from 'react';
import { v4 as uuidv4 } from 'uuid';
import logo from './logo.jpg';

function App() {
  const [prompt, setPrompt] = useState('');
  const [file, setFile] = useState(null);
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('chat');
  const [workspaces, setWorkspaces] = useState([]);
  const [currentWorkspace, setCurrentWorkspace] = useState(null);
  const [userId] = useState(uuidv4());
  const [faqs, setFaqs] = useState([]);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    fetchFAQs();
    fetchWorkspaces();
  }, []);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const fetchFAQs = async () => {
    try {
      const response = await fetch('http://127.0.0.1:8000/faq');
      const data = await response.json();
      setFaqs(data);
    } catch (err) {
      console.error('Failed to fetch FAQs:', err);
    }
  };

  const fetchWorkspaces = async () => {
    try {
      const response = await fetch(`http://127.0.0.1:8000/workspaces/${userId}`);
      const data = await response.json();
      setWorkspaces(data);
    } catch (err) {
      console.error('Failed to fetch workspaces:', err);
    }
  };

  const createWorkspace = async () => {
    try {
      const response = await fetch('http://127.0.0.1:8000/workspaces', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          user_id: userId,
          name: `Workspace ${workspaces.length + 1}`
        })
      });
      const newWorkspace = await response.json();
      setWorkspaces([...workspaces, newWorkspace]);
      setCurrentWorkspace(newWorkspace);
      setMessages([]);
    } catch (err) {
      console.error('Failed to create workspace:', err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!prompt.trim() && !file) return;

    setError(null);
    setLoading(true);

    const formData = new FormData();
    formData.append('prompt', prompt);
    if (file) formData.append('file', file);

    // Add workspace and user ID if needed
    formData.append('user_id', userId);
    if (currentWorkspace?.id) {
      formData.append('workspace_id', currentWorkspace.id);
    }

    const userMessage = {
      role: 'user',
      text: prompt,
      timestamp: new Date().toISOString(),
    };
    setMessages(prev => [...prev, userMessage]);

    try {
      const response = await fetch('http://127.0.0.1:8000/chat/', {
        method: 'POST',
        body: formData
      });

      const data = await response.json();

      const botMessage = {
        role: 'bot',
        text: data[data.length - 1]?.text || 'No response',
        timestamp: new Date().toISOString()
      };
      setMessages(prev => [...prev, botMessage]);
    } catch (err) {
      console.error(err);
      setError('âŒ Failed to connect to backend.');
    } finally {
      setLoading(false);
      setPrompt('');
      setFile(null);
    }
  };

  const renderFile = (msg) => {
    if (!msg.file_url) return null;
    if (msg.file_type === 'image') {
      return (
        <img
          src={`http://127.0.0.1:8000${msg.file_url}`}
          alt="uploaded"
          className="mt-2 max-w-xs rounded"
        />
      );
    }
    return (
      <a
        href={`http://127.0.0.1:8000${msg.file_url}`}
        target="_blank"
        rel="noopener noreferrer"
        className="text-blue-600 underline block mt-2"
      >
        {msg.file_url.split('/').pop()}
      </a>
    );
  };

  const TabButton = ({ id, label, active, onClick }) => (
    <button
      onClick={() => onClick(id)}
      className={`px-4 py-2 rounded-lg font-medium transition-colors ${
        active
          ? 'bg-purple-600 text-white'
          : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
      }`}
    >
      {label}
    </button>
  );

  const renderContent = () => {
    switch (activeTab) {
      case 'chat':
        return (
          <div className="h-96 overflow-y-auto px-2">
            {messages.map((msg, index) => (
              <div
                key={index}
                className={`mb-4 p-3 max-w-[80%] whitespace-pre-line rounded-xl text-sm shadow-md ${
                  msg.role === 'user'
                    ? 'ml-auto bg-purple-100 text-right text-purple-800'
                    : 'mr-auto bg-gray-100 text-left text-gray-800'
                }`}
              >
                {msg.text}
                {renderFile(msg)}
                {msg.timestamp && (
                  <div className="text-xs text-gray-500 mt-1">
                    {new Date(msg.timestamp).toLocaleTimeString()}
                  </div>
                )}
              </div>
            ))}
            {loading && (
              <div className="text-center text-gray-500">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-purple-600 mx-auto"></div>
                <p className="mt-2">Analyzing your requirements...</p>
              </div>
            )}
            <div ref={messagesEndRef} />
          </div>
        );

      case 'workspaces':
        return (
          <div className="h-96 overflow-y-auto px-2">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-lg font-bold">Your Workspaces</h3>
              <button
                onClick={createWorkspace}
                className="bg-green-600 text-white px-3 py-1 rounded-lg hover:bg-green-700"
              >
                + New Workspace
              </button>
            </div>
            {workspaces.map((workspace) => (
              <div
                key={workspace.id}
                className={`p-3 mb-2 rounded-lg border cursor-pointer transition-colors ${
                  currentWorkspace?.id === workspace.id
                    ? 'bg-purple-100 border-purple-300'
                    : 'bg-gray-50 border-gray-200 hover:bg-gray-100'
                }`}
                onClick={() => setCurrentWorkspace(workspace)}
              >
                <h4 className="font-medium">{workspace.name}</h4>
                <p className="text-sm text-gray-600">
                  Created: {new Date(workspace.created_at).toLocaleString()}
                </p>
              </div>
            ))}
          </div>
        );

      case 'faq':
        return (
          <div className="h-96 overflow-y-auto px-2">
            <h3 className="text-lg font-bold mb-4">Frequently Asked Questions</h3>
            {faqs.map((faq, index) => (
              <div key={index} className="mb-4 p-3 bg-gray-50 rounded-lg">
                <h4 className="font-medium text-purple-700 mb-2">{faq.question}</h4>
                <p className="text-sm text-gray-700">{faq.answer}</p>
              </div>
            ))}
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-white to-purple-50 flex flex-col items-center px-4 py-6">
      <div className="flex items-center gap-3 mb-4">
        <img src={logo} alt="Caze Labs" className="w-10 h-10 rounded-full shadow" />
        <h1 className="text-3xl font-bold text-purple-700">LLM Recommender</h1>
      </div>

      <div className="w-full max-w-4xl bg-white rounded-xl shadow-xl p-6 flex flex-col gap-4 border border-gray-200">
        <div className="flex space-x-2 border-b border-gray-200 pb-2">
          <TabButton id="chat" label="ðŸ’¬ Chat" active={activeTab === 'chat'} onClick={setActiveTab} />
          <TabButton id="workspaces" label="ðŸ“ Workspaces" active={activeTab === 'workspaces'} onClick={setActiveTab} />
          <TabButton id="faq" label="â“ FAQ" active={activeTab === 'faq'} onClick={setActiveTab} />
        </div>

        {currentWorkspace && (
          <div className="bg-purple-50 p-2 rounded-lg text-sm">
            <strong>Current Workspace:</strong> {currentWorkspace.name}
          </div>
        )}

        {renderContent()}

        {activeTab === 'chat' && (
          <form onSubmit={handleSubmit} className="flex gap-2 items-center w-full">
            <textarea
              className="flex-1 p-3 border border-gray-300 rounded-lg resize-none shadow-sm focus:outline-purple-500"
              placeholder="Describe your LLM requirements..."
              rows={2}
              value={prompt}
              onChange={(e) => setPrompt(e.target.value)}
            />
            <input
              type="file"
              id="file-input"
              className="hidden"
              onChange={(e) => setFile(e.target.files[0])}
            />
            <label
              htmlFor="file-input"
              className="cursor-pointer bg-purple-100 hover:bg-purple-200 text-purple-700 font-semibold py-2 px-4 rounded-lg text-sm"
            >
              ðŸ“Ž File
            </label>
            <button
              type="submit"
              className="bg-purple-600 text-white px-4 py-2 rounded-lg hover:bg-purple-700 disabled:opacity-50"
              disabled={loading}
            >
              {loading ? 'Analyzing...' : 'Send'}
            </button>
          </form>
        )}

        {file && <p className="text-sm text-gray-500 ml-2">ðŸ“„ {file.name}</p>}
        {error && <p className="text-red-500 text-sm text-center">{error}</p>}
      </div>

      <footer className="mt-6 text-xs text-gray-400">
        Made with ðŸ’œ at Caze Labs | LLM Recommender v1.0.0
      </footer>
    </div>
  );
}

export default App;
