import React, { useState } from 'react';
import logo from './logo.jpg'; // Caze Labs logo

function App() {
  const [prompt, setPrompt] = useState('');
  const [file, setFile] = useState(null);
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!prompt.trim()) return;
    setError(null);
    setLoading(true);

    const userMessage = { role: 'user', text: prompt };
    setMessages((prev) => [...prev, userMessage]);

    try {
      const response = await fetch('http://127.0.0.1:8000/recommend', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ prompt })
      });

      const data = await response.json();
      if (data.models && data.models.length > 0) {
        const model = data.models[0];
        const botMessage = {
          role: 'bot',
          text: `üìå **Model:** ${model.name}\nüìÑ **License:** ${model.license}\nüí° **Why:** ${model.reason}`
        };
        setMessages((prev) => [...prev, botMessage]);
      } else {
        setError('No model recommendation received.');
      }
    } catch (err) {
      console.error(err);
      setError('‚ùå Failed to connect to backend.');
    } finally {
      setLoading(false);
      setPrompt('');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-white to-purple-50 flex flex-col items-center px-4 py-6">
      <div className="flex items-center gap-3 mb-4">
        <img src={logo} alt="Caze Labs" className="w-10 h-10 rounded-full shadow" />
        <h1 className="text-3xl font-bold text-purple-700">LLM Recommender</h1>
      </div>

      <div className="w-full max-w-3xl bg-white rounded-xl shadow-xl p-6 flex flex-col gap-4 border border-gray-200">
        <div className="h-96 overflow-y-auto px-2">
          {messages.map((msg, index) => (
            <div
              key={index}
              className={`mb-4 p-3 max-w-[80%] whitespace-pre-line rounded-xl text-sm shadow-md ${
                msg.role === 'user' ? 'ml-auto bg-purple-100 text-right text-purple-800' : 'mr-auto bg-gray-100 text-left text-gray-800'
              }`}
            >
              {msg.text}
            </div>
          ))}
        </div>

        <form onSubmit={handleSubmit} className="flex gap-2 items-center">
          <textarea
            className="flex-1 p-3 border border-gray-300 rounded-lg resize-none shadow-sm focus:outline-purple-500"
            placeholder="Describe your use case..."
            rows={2}
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
          ></textarea>
          <button
            type="submit"
            className="bg-purple-600 text-white px-4 py-2 rounded-lg hover:bg-purple-700"
            disabled={loading}
          >
            {loading ? 'Thinking...' : 'Ask ü§ñ'}
          </button>
        </form>

        {error && <p className="text-red-500 text-sm text-center">{error}</p>}
      </div>

      <footer className="mt-6 text-xs text-gray-400">Made with üíú at Caze Labs</footer>
    </div>
  );
}

export default App;
