import React, { useState, useEffect, useRef } from 'react';
import { v4 as uuidv4 } from 'uuid';
import { Plus, MessageSquare, Paperclip, ArrowUp, User, Bot, X } from 'lucide-react';
import companyLogo from './logo.jpg';

// --- Main App Component ---
function App() {
  const [conversations, setConversations] = useState({});
  const [activeConversationId, setActiveConversationId] = useState(null);
  const [prompt, setPrompt] = useState('');
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [userId] = useState(() => uuidv4());
  const messagesEndRef = useRef(null);
  const fileInputRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [conversations, activeConversationId]);

  const createNewConversation = () => {
    const newId = uuidv4();
    setConversations(prev => ({ ...prev, [newId]: { id: newId, name: `New Chat`, messages: [] } }));
    setActiveConversationId(newId);
    setFile(null);
  };

  useEffect(() => {
    if (Object.keys(conversations).length === 0) {
      createNewConversation();
    }
  }, []);

  // --- NEW "DUAL AI" SUBMIT LOGIC ---
  const handleSubmit = async (e) => {
    e.preventDefault();
    if ((!prompt.trim() && !file) || !activeConversationId) return;

    setLoading(true);

    const userMessage = {
      id: uuidv4(),
      role: 'user',
      text: prompt,
      file: file ? { name: file.name } : null,
      timestamp: new Date().toISOString(),
    };

    const currentMessages = conversations[activeConversationId]?.messages || [];
    const updatedName = currentMessages.length === 0 ? (prompt.substring(0, 25) + '...') : conversations[activeConversationId].name;

    setConversations(prev => ({
      ...prev,
      [activeConversationId]: { ...prev[activeConversationId], name: updatedName, messages: [...currentMessages, userMessage] }
    }));

    const tempPrompt = prompt;
    const tempFile = file;
    setPrompt('');
    setFile(null);

    // --- SMART ROUTER LOGIC ---
    const isRecommendationRequest = /model|recommend|llm|chatbot|suggest/i.test(tempPrompt);

    if (tempFile || isRecommendationRequest) {
      // --- ROUTE 1: Use Local Backend for Files & Recommendations ---
      console.log("Routing to: Local Backend (Ollama)");
      await handleLocalBackendRequest(tempPrompt, tempFile);
    } else {
      // --- ROUTE 2: Use Cloud AI for General Conversation ---
      console.log("Routing to: Cloud AI (Gemini)");
      await handleGeminiRequest(tempPrompt);
    }
    setLoading(false);
  };

  const handleLocalBackendRequest = async (prompt, file) => {
    const formData = new FormData();
    formData.append('prompt', prompt);
    formData.append('user_id', userId);
    if (file) {
      formData.append('file', file);
    }
    try {
      const response = await fetch('http://127.0.0.1:8000/chat/', { method: 'POST', body: formData });
      if (!response.ok) throw new Error(`Server error: ${response.status}`);
      const data = await response.json();
      const botMessageData = data[data.length - 1];
      addBotMessage(botMessageData.text, botMessageData.id, botMessageData.timestamp);
    } catch (err) {
      addBotMessage(`âŒ Error connecting to local backend: ${err.message}`);
    }
  };

  const handleGeminiRequest = async (prompt) => {
    // This uses a proxy provided by the environment to keep the key secure
    const GEMINI_API_URL = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.0-flash:generateContent?key=`;

    const payload = {
      contents: [{
        parts: [{ text: `You are a helpful AI assistant from Caze Labs. Respond to this prompt: "${prompt}"` }]
      }]
    };

    try {
      const response = await fetch(GEMINI_API_URL, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      if (!response.ok) throw new Error(`Gemini API error: ${response.status}`);
      const data = await response.json();
      const text = data.candidates[0].content.parts[0].text;
      addBotMessage(text);
    } catch (err) {
      addBotMessage(`âŒ Error connecting to Cloud AI: ${err.message}`);
    }
  };

  const addBotMessage = (text, id = uuidv4(), timestamp = new Date().toISOString()) => {
      const botMessage = { id, role: 'bot', text, timestamp };
      setConversations(prev => ({
        ...prev,
        [activeConversationId]: { ...prev[activeConversationId], messages: [...(prev[activeConversationId]?.messages || []), botMessage] }
      }));
  };

  const activeMessages = conversations[activeConversationId]?.messages || [];

  return (
    <>
      <style>{`.logo-pulse { animation: pulse 4s ease-in-out infinite; } @keyframes pulse { 0%, 100% { transform: scale(1); } 50% { transform: scale(1.05); } }`}</style>
      <div className="flex h-screen w-screen bg-gray-800 text-white font-sans">
        <Sidebar conversations={Object.values(conversations)} activeConversationId={activeConversationId} setActiveConversationId={setActiveConversationId} createNewConversation={createNewConversation} />
        <ChatView messages={activeMessages} loading={loading} prompt={prompt} setPrompt={setPrompt} setFile={setFile} handleSubmit={handleSubmit} messagesEndRef={messagesEndRef} fileInputRef={fileInputRef} file={file} />
      </div>
    </>
  );
}

