import React, { useState } from 'react';

function App() {
  const [prompt, setPrompt] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Prompt submitted:', prompt);
    // Youâ€™ll later send this to backend
  };

  return (
    <div className="min-h-screen bg-gray-100 p-4">
      <h1 className="text-3xl font-bold text-blue-600 text-center mb-6">LLM Recommender ðŸ¤–</h1>

      <form onSubmit={handleSubmit} className="max-w-xl mx-auto bg-white p-4 rounded-lg shadow">
        <label className="block mb-2 text-gray-700 font-medium">Enter your use case or prompt:</label>
        <textarea
          className="w-full p-3 border border-gray-300 rounded mb-4"
          rows={4}
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          placeholder="e.g., Summarize a legal document"
        ></textarea>

        <div className="flex justify-between items-center">
          <input type="file" className="border p-2 rounded" />
          <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
            Get LLMs
          </button>
        </div>
      </form>
    </div>
  );
}

export default App;
